CREATE TABLE IF NOT EXISTS Member (
 UID TEXT PRIMARY KEY,
 Name TEXT NOT NULL,
 Recently TEXT,
 Email TEXT NOT NULL,
 Redeem INTEGER NOT NULL,
 Time TEXT
);

CREATE TABLE IF NOT EXISTS Product (
 PID INT PRIMARY KEY NOT NULL,
 Name TEXT NOT NULL,
 Price INT NOT NULL,
 Location TEXT NOT NULL,
 FOREIGN KEY (Name) REFERENCES Member (Name)
);